import useInput from "./hooks/use-input";
import "./styles.css";
export default function Form(){

    const {
        inputVal: name,
        isValid: nameIsValid,
        hasError: nameHasError,
        touchFn: nameTouchFn,
        handleBlur: handleNameBlur,
        handleChange: handleNameChange,
        reset: resetName,
    } = useInput((inputValue)=>inputValue.trim()!=="");

    const {
        inputVal: email,
        isValid: emailIsValid,
        hasError: emailHasError,
        touchFn: emailTouchFn,
        handleBlur: handleEmailBlur,
        handleChange: handleEmailChange,
        reset: resetEmail,
    } = useInput((inputValue)=>inputValue.includes("@"));

    const {
        inputVal: password,
        isValid: passwordIsValid,
        hasError: passwordHasError,
        touchFn: passwordTouchFn,
        handleBlur: handlePasswordBlur,
        handleChange: handlePasswordChange,
        reset: resetPassword,
    } = useInput((inputValue)=>inputValue.trim().length>6);

    let isFormValid=false;

    if(nameIsValid && emailIsValid && passwordIsValid){
        isFormValid=true;
    }

    function handleSubmit(event){
        event.preventDefault();
        const form= new FormData(event.target);
        const formData=Object.fromEntries(form);
        console.log(formData);
    }
    return <form className="form" onSubmit={handleSubmit} >
        <div className="input-grp">
            <label htmlFor="name">Name</label>
            <input type="text" id="name" name="name" onBlur={handleNameBlur} onChange={handleNameChange} />
        </div>
        <div className="input-grp">
            <label htmlFor="email">Email</label>
            <input type="email" id="email" name="email" onBlur={handleEmailBlur} onChange={handleEmailChange}/>
        </div>
        <div className="input-grp">
            <label htmlFor="password">password</label>
            <input type="password" id="password" name="password" onBlur={handlePasswordBlur} onChange={handlePasswordChange}/>
        </div>
        <div className="actions">
            <button type="reset">Reset</button>
            <button type="submit">Submit</button>
        </div>
    </form>
}
